import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./manage-order.css";

export default function OrdersPage() {
  const { userId } = useParams();
  const { t } = useTranslation();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(
          `http://localhost:5001/api/orders/${userId}`
        );
        if (!response.ok) {
          throw new Error(`Error fetching orders: ${response.statusText}`);
        }
        const data = await response.json();

        const allOrders = [
          ...data.pending.map((order) => ({ ...order, status: "Pending" })),
          ...data.success.map((order) => ({ ...order, status: "Success" })),
          ...data.failed.map((order) => ({ ...order, status: "Failed" })),
        ].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

        setOrders(allOrders);
      } catch (err) {
        setError(t('errorFetching', { error: err.message }));
      }
    };

    fetchOrders();
  }, [userId, t]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="orders-page">
      <Header />
      <div className="manage-orders">
        <table className="order-table">
          <thead>
            <tr>
              <th>{t('cropName')}</th>
              <th>{t('quantity')}</th>
              <th>{t('price')}</th>
              <th>{t('status')}</th>
              <th>{t('date')}</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id} className={getStatusClass(order.status)}>
                <td>{t(order.cropId.name)}</td>
                <td>{order.quantity}</td>
                <td>{order.price}</td>
                <td>{t(order.status)}</td>
                <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                <td>
                  <button onClick={() => trackOrder(order._id)}>Track Order</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="legend">
        <div className="legend-item pending">{t('pending')}</div>
        <div className="legend-item success">{t('success')}</div>
        <div className="legend-item failed">{t('failed')}</div>
      </div>

      <Footer />
    </div>
  );
}

function getStatusClass(status) {
  if (status === "Pending") return "text-yellow";
  if (status === "Success") return "text-green";
  if (status === "Failed") return "text-red";
  return "";
}

function trackOrder(orderId) {
  console.log(`Tracking order: ${orderId}`);
}
