import React from "react";
import "./header.css";
import { Link, useNavigate, useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import LanguageSwitcher from "../components/LanguageSwitcher";

export default function UserHeader() {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const { userId, orderId, pay, transactionId } = useParams();

  const handleLogout = (e) => {
    e.preventDefault();
    navigate("/");
    alert("You have successfully logged out\nThank you for your support ðŸ˜Š");
  };

  return (
    <div className="main">
      <div className="main-container">
        <h2 className="logo">ðŸŒ¿FMSðŸŒ¿</h2>
        <div className="links">
          | <Link className="link" to="/"> Home </Link> |
          <Link className="link" to="/explore"> Explore </Link> |
          <Link className="link" to={`/manageOrders/${userId}`}> Manage Orders </Link> |
          <Link className="link" to={`/buyCrops/${userId}/${orderId || ""}/${pay || ""}/${transactionId || ""}`}>
            Buy Crops
          </Link> |
          <Link className="link" to="/" onClick={handleLogout}> Logout </Link> |
          <div className="language-switcher">
            <LanguageSwitcher />
          </div>
        </div>
      </div>
      <marquee className="marquee-block">
        {t("marquee-text")}
      </marquee>
    </div>
  );
}
