import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./manage-order.css";

export default function OrdersPage() {
  const { userId } = useParams();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/orders/${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching orders: ${response.statusText}`);
        }
        const data = await response.json();

        // Merge all orders into a single array, sorted by date (latest first)
        const allOrders = [
          ...data.pending.map(order => ({ ...order, status: 'Pending' })),
          ...data.success.map(order => ({ ...order, status: 'Success' })),
          ...data.failed.map(order => ({ ...order, status: 'Failed' }))
        ].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

        setOrders(allOrders);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchOrders();
  }, [userId]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="orders-page">
      <Header />
      <div className="manage-orders"></div>
      <table className="order-table">
        <thead>
          <tr>
            <th>Crop Name</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Status</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id} className={getStatusClass(order.status)}>
              <td>{order.cropId.name}</td>
              <td>{order.quantity}</td>
              <td>{order.price}</td>
              <td>{order.status}</td>
              <td>{new Date(order.createdAt).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="legend">
        <div className="legend-item pending">Pending</div>
        <div className="legend-item success">Success</div>
        <div className="legend-item failed">Failed</div>
      </div>
      <Footer />
    </div>
  );
}

function getStatusClass(status) {
  if (status === 'Pending') return 'text-yellow';
  if (status === 'Success') return 'text-green';
  if (status === 'Failed') return 'text-red';
  return '';
}
