import React, { useState } from 'react';
import './SoilInputForm.css';

export default function SoilInputForm() {
  const [files, setFiles] = useState([]);
  const [currentFiles, setCurrentFiles] = useState([]);

  const handleFileChange = (event) => {
    const newFiles = Array.from(event.target.files);
    setCurrentFiles((prevFiles) => [...prevFiles, ...newFiles]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (currentFiles.length === 0) {
      alert('Please upload at least one PDF file.');
      return;
    }

    const promises = currentFiles.map(async (file) => {
      const formData = new FormData();
      formData.append('file', file);

      try {
        const response = await fetch('http://127.0.0.1/predict', {
          method: 'POST',
          body: formData,
        });

        if (!response.ok) {
          throw new Error('Failed to fetch prediction');
        }

        const data = await response.json();
        return { file: file.name, prediction: data.predicted_crop };
      } catch (error) {
        console.error('Error:', error);
        alert('An error occurred while predicting the crop. Please try again.');
        return null;
      }
    });

    const results = await Promise.all(promises);
    const validResults = results.filter((result) => result !== null);

    setFiles((prevFiles) => [...prevFiles, ...validResults]);
    setCurrentFiles([]); // Clear files after submission
  };

  return (
    <div className="soil-input-form">
      <div className="chat-window">
        {files.map((item, index) => (
          <div className="chat-entry" key={index}>
            <div className="file-name">{item.file}</div>
            <iframe
              src={URL.createObjectURL(new Blob([item.file]))}
              title={`PDF Preview ${index}`}
              className="pdf-preview"
            />
            <div className="prediction">
              <strong>Prediction:</strong> {item.prediction}
            </div>
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit} className="input-form">
        <input
          type="file"
          accept="application/pdf"
          multiple
          onChange={handleFileChange}
        />
        <button type="submit">Predict Crop</button>
      </form>
    </div>
  );
}
