import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./manage-order.css";

export default function OrdersPage() {
  const { userId } = useParams();
  const { t } = useTranslation();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const [trackedOrderId, setTrackedOrderId] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/orders/${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching orders: ${response.statusText}`);
        }
        const data = await response.json();

        const allOrders = [
          ...data.pending.map((order) => ({ ...order, status: "Pending" })),
          ...data.success.map((order) => ({ ...order, status: "Success" })),
          ...data.failed.map((order) => ({ ...order, status: "Failed" })),
        ].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

        setOrders(allOrders);
      } catch (err) {
        setError(t('errorFetching', { error: err.message }));
      }
    };

    fetchOrders();
  }, [userId, t]);

  const calculateDeliveryDays = (harvestDate) => {
    const harvest = new Date(harvestDate);
    const today = new Date();
    const diffTime = today - harvest;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    let deliveryStage = "";
    if (diffDays < 0) deliveryStage = "Not harvested yet";
    else if (diffDays < 2) deliveryStage = "Crop Harvested";
    else if (diffDays < 4) deliveryStage = "Out for Delivery";
    else deliveryStage = "Delivered";

    return deliveryStage;
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="orders-page">
      <Header />
      <div className="manage-orders">
        <table className="order-table">
          <thead>
            <tr>
              <th>{t('cropName')}</th>
              <th>{t('quantity')}</th>
              <th>{t('price')}</th>
              <th>{t('status')}</th>
              <th>{t('date')}</th>
              <th>Track Order</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <React.Fragment key={order._id}>
                <tr className={getStatusClass(order.status)}>
                  <td>{t(order.cropId.name)}</td>
                  <td>{order.quantity}</td>
                  <td>{order.price}</td>
                  <td>{t(order.status)}</td>
                  <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                  <td>
                    <button onClick={() => setTrackedOrderId(order._id)}>
                      Track Order
                    </button>
                  </td>
                </tr>
                {trackedOrderId === order._id && (
                  <tr>
                    <td colSpan="6">
                      <div className="progress-bar">
                        <div className="progress-step harvested">
                          <div className="step-marker" />
                          <span>Crop Harvested</span>
                        </div>
                        <div className="progress-step out-for-delivery">
                          <div className="step-marker" />
                          <span>Out for Delivery</span>
                        </div>
                        <div className="progress-step delivered">
                          <div className="step-marker" />
                          <span>Delivered</span>
                        </div>
                      </div>
                      <div className="delivery-status">
                        {calculateDeliveryDays(order.cropId.harvestDate)}
                      </div>
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
}

function getStatusClass(status) {
  if (status === "Pending") return "text-yellow";
  if (status === "Success") return "text-green";
  if (status === "Failed") return "text-red";
  return "";
}
