import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./buy-crops.css";

const haversineDistance = (coords1, coords2) => {
  const toRad = (value) => (value * Math.PI) / 180;
  const R = 6371;

  const dLat = toRad(coords2.latitude - coords1.latitude);
  const dLon = toRad(coords2.longitude - coords1.longitude);
  const lat1 = toRad(coords1.latitude);
  const lat2 = toRad(coords2.latitude);

  const a =
    Math.sin(dLat / 2) * Math.sin(dLat / 2) +
    Math.cos(lat1) * Math.cos(lat2) *
    Math.sin(dLon / 2) * Math.sin(dLon / 2);

  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

  return R * c;
};

export default function BuyCrops() {
  const { t } = useTranslation();
  const [cropsOnSale, setCropsOnSale] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [maxDistance, setMaxDistance] = useState(100); // Default max distance
  const [filteredCrops, setFilteredCrops] = useState([]);
  const [filterApplied, setFilterApplied] = useState(false); // Track if filter is applied

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ latitude, longitude });
      },
      (error) => {
        console.error("Error fetching user location:", error);
      }
    );

    fetch("http://localhost:5001/api/commerce/getAllCropsOnSale")
      .then((response) => response.json())
      .then((data) => {
        setCropsOnSale(data);
        setFilteredCrops(data); // Initialize filtered crops
      })
      .catch((error) => console.error(error));
  }, []);

  useEffect(() => {
    // Filter crops based on distance when maxDistance or cropsOnSale changes
    if (userLocation) {
      const newFilteredCrops = cropsOnSale.filter((crop) => {
        const cropLocation = {
          latitude: crop.location.coordinates[1],
          longitude: crop.location.coordinates[0],
        };
        const distance = haversineDistance(userLocation, cropLocation);
        return distance <= maxDistance;
      });
      setFilteredCrops(newFilteredCrops);
      setFilterApplied(newFilteredCrops.length < cropsOnSale.length); // Check if filter is applied
    }
  }, [maxDistance, cropsOnSale, userLocation]);

  const handleBuy = async (cropId) => {
    const confirmation = window.confirm(t("confirm_buy"));
    if (!confirmation) return;

    const qty = window.prompt(t("enter_quantity")); // Ask for quantity
    if (qty === null) return; // User canceled
    const quantity = parseInt(qty, 10);

    if (isNaN(quantity) || quantity <= 0) {
      alert(t("invalid_quantity")); // Notify user of invalid quantity
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5001/api/commerce/buyCrop/${cropId}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ quantity }), // Send the quantity in the request
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      alert(t("purchase_success"));

      setFilteredCrops(filteredCrops.filter((crop) => crop._id !== cropId));
      setCropsOnSale(cropsOnSale.filter((crop) => crop._id !== cropId));
    } catch (error) {
      console.error("There was a problem with the fetch operation:", error);
    }
  };

  const toggleFilter = () => {
    if (filterApplied) {
      setMaxDistance(100000000000000);
    } else {
      setMaxDistance(maxDistance); // Keep current distance
    }
    setFilterApplied(!filterApplied); // Toggle filter status
  };

  return (
    <div className="buy-crops-main">
      <Header />
      <div className="buy-crops">
        <div className="filter-section">
          <input
            type="number"
            value={maxDistance}
            onChange={(e) => setMaxDistance(e.target.value)}
            placeholder={t("max_distance_placeholder")} // You can define this translation
          />
          <button onClick={toggleFilter}>
            {filterApplied ? t("remove_filter") : t("filter")}
          </button>
        </div>
        <table>
          <thead>
            <tr>
              <th>{t("table.name")}</th>
              <th>{t("table.variety")}</th>
              <th>{t("table.harvestDate")}</th>
              <th>{t("table.quantity")}</th>
              <th>{t("table.pricePerUnit")}</th>
              <th>{t("table.distance")}</th>
              <th>{t("table.actions")}</th>
            </tr>
          </thead>
          <tbody>
            {filteredCrops.map((crop) => {
              const cropLocation = {
                latitude: crop.location.coordinates[1],
                longitude: crop.location.coordinates[0],
              };
              const distance =
                userLocation && cropLocation
                  ? haversineDistance(userLocation, cropLocation).toFixed(2)
                  : "N/A";

              return (
                <tr key={crop._id}>
                  <td>{t(crop.name)}</td>
                  <td>{t(crop.variety)}</td>
                  <td>{new Date(crop.harvestDate).toLocaleDateString()}</td>
                  <td>{crop.quantity - crop.soldQty}</td>
                  <td>{crop.pricePerUnit}</td>
                  <td>{distance} km</td>
                  <td>
                    <button onClick={() => handleBuy(crop._id)}>
                      {t("table.buy")}
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
}
