import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "./manage-order.css";

export default function OrdersPage() {
  const { userId } = useParams();
  const [orders, setOrders] = useState({ pending: [], success: [], failed: [] });
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/orders/${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching orders: ${response.statusText}`);
        }
        const data = await response.json();
        setOrders(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchOrders();
  }, [userId]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="orders-page">
      <h2>Orders</h2>

      <div className="order-table">
        <OrderTableSection title="Pending Orders" orders={orders.pending} color="yellow" />
        <OrderTableSection title="Successful Orders" orders={orders.success} color="green" />
        <OrderTableSection title="Failed Orders" orders={orders.failed} color="red" />
      </div>
    </div>
  );
}

function OrderTableSection({ title, orders, color }) {
  return (
    <div className={`order-section ${color}`}>
      <h3>{title}</h3>
      <table>
        <thead>
          <tr>
            <th>Crop Name</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Status</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order) => (
            <tr key={order._id}>
              <td>{order.cropId.name}</td>
              <td>{order.quantity}</td>
              <td>{order.price}</td>
              <td>{order.status}</td>
              <td>{new Date(order.createdAt).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
