import React, { useEffect, useState } from "react";
import { useTranslation } from "react-i18next";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./home.css";

const Home = () => {
  const { t } = useTranslation();
  
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  const handleMouseMove = (e) => {
    setMousePosition({ x: e.clientX, y: e.clientY });
  };

  useEffect(() => {
    window.addEventListener("mousemove", handleMouseMove);
    
    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <div className="Home">
      <Header />
      <div className="home-main">
        <div className="slider">
          <img src="https://img.freepik.com/free-photo/healthy-eating-fresh-vegetables-rustic-wicker-basket-generated-by-ai_188544-37704.jpg?t=st=1727603601~exp=1727607201~hmac=5d02fa2ece01e6f530ec42f94984230e655deae65819d1c9849983a484445c05&w=1380" alt={t("imageAlt1")} className="slide" />
          <img src="https://img.freepik.com/free-photo/healthy-eating-fresh-vegetables-rustic-wicker-basket-generated-by-ai_188544-37704.jpg?t=st=1727603601~exp=1727607201~hmac=5d02fa2ece01e6f530ec42f94984230e655deae65819d1c9849983a484445c05&w=1380" alt={t("imageAlt2")} className="slide" />
          <img src="https://img.freepik.com/free-photo/healthy-eating-fresh-vegetables-rustic-wicker-basket-generated-by-ai_188544-37704.jpg?t=st=1727603601~exp=1727607201~hmac=5d02fa2ece01e6f530ec42f94984230e655deae65819d1c9849983a484445c05&w=1380" alt={t("imageAlt3")} className="slide" />
        </div>
        <h2 className="tagline">{t("tagline")}</h2>
        <div className="features">
          <h3>{t("features")}</h3>
          <ul>
            <li className="feature-item">{t("feature1")}</li>
            <li className="feature-item">{t("feature2")}</li>
            <li className="feature-item">{t("feature3")}</li>
            <li className="feature-item">{t("feature4")}</li>
          </ul>
        </div>
      </div>
      <Footer />
      <div className="mouse-tracker" style={{ left: mousePosition.x, top: mousePosition.y }}></div>
    </div>
  );
};

export default Home;
