import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./manage-order.css";

export default function OrdersPage() {
  const { userId } = useParams();
  const { t } = useTranslation();
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState(null);
  const [trackingInfo, setTrackingInfo] = useState({}); // To store tracking data

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/orders/${userId}`);
        if (!response.ok) {
          throw new Error(`Error fetching orders: ${response.statusText}`);
        }
        const data = await response.json();

        const allOrders = [
          ...data.pending.map((order) => ({ ...order, status: "Pending" })),
          ...data.success.map((order) => ({ ...order, status: "Success" })),
          ...data.failed.map((order) => ({ ...order, status: "Failed" })),
        ].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

        setOrders(allOrders);
      } catch (err) {
        setError(t('errorFetching', { error: err.message }));
      }
    };

    fetchOrders();
  }, [userId, t]);

  const handleTrackOrder = (order) => {
    const harvestDate = new Date(order.cropId.harvestDate);
    const currentDate = new Date();
    
    const daysSinceHarvest = Math.floor((currentDate - harvestDate) / (1000 * 60 * 60 * 24));

    let deliveryDays = 0;
    if (daysSinceHarvest < 0) {
      deliveryDays = 0; // Harvest date is in the future
    } else if (daysSinceHarvest < 2) {
      deliveryDays = 1; // Out for delivery after 2 days
    } else {
      const distance = 150; // Example distance in km
      deliveryDays = Math.ceil(distance / 150); // 1 day for 150km
    }

    setTrackingInfo({
      ...trackingInfo,
      [order._id]: {
        progress: Math.min(daysSinceHarvest, deliveryDays) * (100 / deliveryDays),
        daysUntilDelivery: deliveryDays - daysSinceHarvest,
      },
    });
  };

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="orders-page">
      <Header />
      <div className="manage-orders">
        <table className="order-table">
          <thead>
            <tr>
              <th>{t('cropName')}</th>
              <th>{t('quantity')}</th>
              <th>{t('price')}</th>
              <th>{t('status')}</th>
              <th>{t('date')}</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order._id} className={getStatusClass(order.status)}>
                <td>{order.cropId.name}</td>
                <td>{order.quantity}</td>
                <td>{order.price}</td>
                <td>{t(order.status)}</td>
                <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                <td>
                  <button onClick={() => handleTrackOrder(order)}>Track Order</button>
                  {trackingInfo[order._id] && (
                    <div>
                      <div style={{ width: `${trackingInfo[order._id].progress}%`, backgroundColor: 'blue', height: '10px' }} />
                      <span>{trackingInfo[order._id].daysUntilDelivery} days until delivery</span>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
}

function getStatusClass(status) {
  if (status === "Pending") return "text-yellow";
  if (status === "Success") return "text-green";
  if (status === "Failed") return "text-red";
  return "";
}
