import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { useParams } from "react-router-dom";
import Header from "../header-footer/header";
import Footer from "../header-footer/footer";
import "./manage-crops.css";

export default function CropList() {
  const { userId } = useParams();
  const { t } = useTranslation();
  const [crops, setCrops] = useState([]);
  const [editingCropId, setEditingCropId] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:5001/api/commerce/getallcrops/${userId}`)
      .then((response) => response.json())
      .then((data) => setCrops(data))
      .catch((error) => console.error(error));
  }, [userId]);

  const handleSell = async (cropId) => {
    alert("Do you want to add the crop on sale? You can't update crops again.");
    if (!confirmation) return;

    try {
      const response = await fetch(
        `http://localhost:5001/api/commerce/updateCrop/${cropId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            status: "selling",
            sell: true,
          }),
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setCrops(crops.map((crop) => (crop._id === cropId ? data.crop : crop)));
    } catch (error) {
      console.error("There was a problem with the fetch operation:", error);
    }
  };

  const handleEdit = (cropId) => {
    setEditingCropId(cropId);
  };

  const handleSave = async (cropId, updatedCrop) => {
    try {
      const response = await fetch(
        `http://localhost:5001/api/commerce/updateCrop/${cropId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(updatedCrop),
        }
      );

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setCrops(crops.map((crop) => (crop._id === cropId ? data.crop : crop)));
      setEditingCropId(null);
    } catch (error) {
      console.error("There was a problem with the fetch operation:", error);
    }
  };

  return (
    <div className="crops-main">
      <Header />
      <div className="manage-crops">
        <table className={editingCropId ? "editing" : ""}>
          <thead>
            <tr>
              <th>{t("table.name")}</th>
              <th>{t("table.variety")}</th>
              <th>{t("table.category")}</th>
              <th>{t("table.label")}</th>
              <th>{t("table.harvestDate")}</th>
              <th>{t("table.quantity")}</th>
              <th>{t("table.unit")}</th>
              <th>{t("table.pricePerUnit")}</th>
              <th>{t("table.actions")}</th>
            </tr>
          </thead>
          <tbody>
            {crops.map((crop) => {
              const isOutOfStock = crop.quantity === 0; // Check if the crop is out of stock

              return (
                <tr key={crop._id}>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="text"
                        disabled
                        value={crop.name}
                      />
                    ) : (
                      crop.name
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="text"
                        disabled
                        value={crop.variety}
                      />
                    ) : (
                      crop.variety
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="text"
                        disabled
                        value={crop.category}
                      />
                    ) : (
                      crop.category
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="text"
                        disabled
                        value={crop.label}
                      />
                    ) : (
                      crop.label
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="date"
                        value={crop.harvestDate}
                      />
                    ) : (
                      new Date(crop.harvestDate).toLocaleDateString()
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="number"
                        value={crop.quantity}
                      />
                    ) : (
                      crop.quantity
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="text"
                        disabled
                        value={crop.unit}
                      />
                    ) : (
                      crop.unit
                    )}
                  </td>
                  <td>
                    {editingCropId === crop._id ? (
                      <input
                        type="number"
                        value={crop.pricePerUnit}
                      />
                    ) : (
                      crop.pricePerUnit
                    )}
                  </td>
                  <td>
                    {isOutOfStock ? ( // Disable all buttons if out of stock
                      <>
                        <button disabled className="out-of-stock">
                          {t("table.outOfStock")}
                        </button>
                      </>
                    ) : (
                      <>
                        {editingCropId === crop._id ? (
                          <button
                            onClick={() =>
                              handleSave(
                                crop._id,
                                crops.find((c) => c._id === crop._id)
                              )
                            }
                          >
                            {t("table.save")}
                          </button>
                        ) : crop.sell === true ? null : (
                          <button
                            onClick={() => handleEdit(crop._id)}
                            className="update"
                          >
                            {t("table.update")}
                          </button>
                        )}
                        {crop.sell === true ? (
                          <button disabled className="on-sale">
                            {t("table.onSale")}
                          </button>
                        ) : (
                          <button
                            onClick={() => handleSell(crop._id)}
                            className="on-sale"
                          >
                            {t("table.sell")}
                          </button>
                        )}
                      </>
                    )}
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
}
