import React, { useState } from 'react';
import './SoilInputForm.css';

export default function SoilInputForm() {
  const [chat, setChat] = useState([]);
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file) {
      alert('Please upload a PDF file.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://127.0.0.1:5000/predict', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to fetch prediction');
      }

      const data = await response.json();
      setChat((prevChat) => [
        ...prevChat,
        { type: 'bot', message: data.predicted_crop },
      ]);
      setFile(null); // Clear file after submission
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while predicting the crop. Please try again.');
    }
  };

  return (
    <div className="soil-input-form">
      <div className="chat-container">
        <div className="chat-window">
          {chat.map((entry, index) => (
            <div className={`chat-entry ${entry.type}`} key={index}>
              {entry.message}
            </div>
          ))}
        </div>
        <form onSubmit={handleSubmit}>
          <input type="file" accept="application/pdf" onChange={handleFileChange} />
          <button type="submit">Predict Crop</button>
        </form>
      </div>
      {file && (
        <iframe src={URL.createObjectURL(file)} title="PDF Preview" className="pdf-preview" />
      )}
    </div>
  );
}
